clamp_max(
  sum by ()(
    sum by (stage_group,component) (
      label_replace(
        sum_over_time(
          gitlab:component:stage_group:execution:apdex:success:rate_1h{environment="$environment",monitor="global",stage="$stage",stage_group="security_policies"}[$__range]
        ), 'sli_kind', 'apdex', '', ''
      )
      or
      label_replace(
        sum_over_time(
          gitlab:component:stage_group:execution:ops:rate_1h{environment="$environment",monitor="global",stage="$stage",stage_group="security_policies"}[$__range]
        )
        -
        sum_over_time(
          gitlab:component:stage_group:execution:error:rate_1h{environment="$environment",monitor="global",stage="$stage",stage_group="security_policies"}[$__range]
        ), 'sli_kind', 'error', '', ''
      )
    ) unless on (stage_group,component) gitlab:ignored_component:stage_group
  )
  /
  sum by ()(
    sum by (stage_group,component) (
      label_replace(
        sum_over_time(
          gitlab:component:stage_group:execution:apdex:weight:score_1h{environment="$environment",monitor="global",stage="$stage",stage_group="security_policies"}[$__range]
        ),
        'sli_kind', 'apdex', '', ''
