targets.expr:
sum by (job,shard) (
  sum by (instance,job,shard) (
    rate(gitlab_runner_jobs_total{job="runners-manager",instance=~"${runner_managers:pipe}"}[$__interval])
  )
  or on (instance)
  sum by (instance,job,shard) (
    rate(gitlab_runner_jobs_total{job!="runners-manager",instance=~"${runner_managers:pipe}"}[$__interval])
  )
) * 60
