targets.expr:
sum by (shard) (
  gitlab_runner_jobs{environment="$environment", stage="$stage", job="runners-manager", instance=~"${runner_manager:pipe}"}
) / sum by (shard) (
  gitlab_runner_concurrent{environment="$environment", stage="$stage", job="runners-manager", instance=~"${runner_manager:pipe}"}
)

sum by (instance) (
  gitlab_runner_jobs{environment="$environment", stage="$stage", job="runners-manager", instance=~"${runner_manager:pipe}"}
) / sum by (instance) (
  gitlab_runner_concurrent{environment="$environment", stage="$stage", job="runners-manager", instance=~"${runner_manager:pipe}"}
)

sum by (instance,runner) (
  gitlab_runner_jobs{environment="$environment", stage="$stage", job="runners-manager", instance=~"${runner_manager:pipe}"}
) / sum by (instance,runner) (
  gitlab_runner_limit{environment="$environment", stage="$stage", job="runners-manager", instance=~"${runner_manager:pipe}"}
)
