targets.expr:
min(
  min_over_time(
    gitlab_service_apdex:ratio{environment="$environment", type="ci-runners", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

max(
  max_over_time(
    gitlab_service_errors:ratio{environment="$environment", type="ci-runners", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

max(
  avg_over_time(
    gitlab_service_ops:rate{environment="$environment", type="ci-runners", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

min(
  min_over_time(
    gitlab_service_apdex:ratio{environment="$environment", type="ci-runners", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

max(
  max_over_time(
    gitlab_service_errors:ratio{environment="$environment", type="ci-runners", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

max(
  avg_over_time(
    gitlab_service_ops:rate{environment="$environment", type="ci-runners", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

clamp_min(
  clamp_max(
    sum without (state, executor_stage, exported_stage) (
  gitlab_runner_jobs{job="private-runners"}
)
/
(
  gitlab_runner_limit{job="private-runners"}
  > 0
)

  ,1)
,0)

clamp_min(
  clamp_max(
    sum without (state, executor_stage, exported_stage) (
  gitlab_runner_jobs{job="shared-runners"}
)
/
(
  gitlab_runner_limit{job="shared-runners"}
  > 0
)

  ,1)
,0)

clamp_min(
  clamp_max(
    sum without (state, executor_stage, exported_stage) (
  gitlab_runner_jobs{job="shared-runners-gitlab-org"}
)
/
(
  gitlab_runner_limit{job="shared-runners-gitlab-org"}
  > 0
)

  ,1)
,0)

max by (type, stage, component) (
  clamp_max(
    gitlab_component_saturation:ratio{environment="$environment", type="ci-runners", stage=~"|$stage"}
    ,
    1
  ) >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
)

sort_desc(
  max by (type, stage, component) (
    clamp_max(
      gitlab_component_saturation:ratio:avg_over_time_1w{
        environment="$environment",
        type="ci-runners", stage=~"|$stage"
      } +
      2 *
        gitlab_component_saturation:ratio:stddev_over_time_1w{
          environment="$environment",
          type="ci-runners", stage=~"|$stage"
        }
      , 1
    )
    >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
  )
)

sort_desc(
  max by (type, stage, component) (
    clamp_max(
      gitlab_component_saturation:ratio:predict_linear_2w{
        environment="$environment",
        type="ci-runners", stage=~"|$stage"
      } +
      2 *
        gitlab_component_saturation:ratio:stddev_over_time_1w{
          environment="$environment",
          type="ci-runners", stage=~"|$stage"
        }
    , 1
    )
    >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
  )
)

clamp_min(clamp_max(
  max(
    gitlab_component_saturation:ratio{
      type="ci-runners",
      environment="$environment",
      stage=~"|$stage"
    }
  ) by (component)
  ,1)
,0)
