targets.expr:
clamp_min(
  clamp_max(
    max by(environment, tier, type, stage, shard, job, instance) (
  clamp_min(
    clamp_max(
      (
        process_open_fds{env="$environment",environment="$environment",fqdn=~"$fqdn",type="gitaly"}
        /
        process_max_fds{env="$environment",environment="$environment",fqdn=~"$fqdn",type="gitaly"}
      )
      or
      (
        ruby_file_descriptors{env="$environment",environment="$environment",fqdn=~"$fqdn",type="gitaly"}
        /
        ruby_process_max_fds{env="$environment",environment="$environment",fqdn=~"$fqdn",type="gitaly"}
      )
      ,
      1)
  ,
  0)
)

  ,1)
,0)

clamp_min(clamp_max(sum(
  rate(
    gitaly_rate_limiting_acquiring_seconds_bucket{
      env="$environment",environment="$environment",fqdn=~"$fqdn",type="gitaly",
      le="60"
    }[$__interval]
  )
) by (environment, type, stage, fqdn, grpc_method)
/
sum(
  rate(
    gitaly_rate_limiting_acquiring_seconds_bucket{
      env="$environment",environment="$environment",fqdn=~"$fqdn",type="gitaly",
      le="+Inf"
    }[$__interval]
  )
) by (environment, type, stage, fqdn, grpc_method)
,1),0)
