targets.expr:
(1-(
    ceil(
  (
    sum by (stage) (
      label_replace(
        sum by (stage)(
          sum by (stage)(
            sum_over_time(
              gitlab:component:stage_group:execution:apdex:weight:score_1h{environment="gprd",monitor="global",stage="main",stage_group="global_search"}[$__range]
            ) -
            # Request with satisfactory apdex
            sum_over_time(
              gitlab:component:stage_group:execution:apdex:success:rate_1h{environment="gprd",monitor="global",stage="main",stage_group="global_search"}[$__range]
            )
          ) unless on (stage_group,component) gitlab:ignored_component:stage_group
        ),
        'violation_type', 'apdex' , '', ''
      )
      or
      label_replace(
        sum by (stage)(
          sum by (stage)(
            sum_over_time(
              gitlab:component:stage_group:execution:error:rate_1h{environment="gprd",monitor="global",stage="main",stage_group="global_search"}[$__range]
            )
          ) unless on (stage_group,component) gitlab:ignored_component:stage_group
        ),
        'violation_type', 'error' , '', ''
      )
    ) > 0
  ) * 60
))/(ceil(
  (
    sum by (stage) (
      label_replace(
        sum by (stage)(
          sum by (stage)(
            sum_over_time(
              gitlab:component:stage_group:execution:apdex:weight:score_1h{environment="gprd",monitor="global",stage="main",stage_group="global_search"}[$__range]
            )
          ) unless on (stage_group,component) gitlab:ignored_component:stage_group
        ),
        'violation_type', 'apdex' , '', ''
      )
      or
      label_replace(
        sum by (stage)(
          sum by (stage)(
            sum_over_time(
              gitlab:component:stage_group:execution:ops:rate_1h{environment="gprd",monitor="global",stage="main",stage_group="global_search"}[$__range]
            )
            and sum_over_time(gitlab:component:stage_group:execution:error:rate_1h{environment="gprd",monitor="global",stage="main",stage_group="global_search"}[$__range])
          ) unless on (stage_group,component) gitlab:ignored_component:stage_group
        ),
        'violation_type', 'error' , '', ''
      )
    ) > 0
  ) * 60
)))*100

