targets.expr:
clamp_min(clamp_max((
  (
    sum by (action) (
  rate(registry_storage_action_seconds_bucket{action!="",env="$environment",environment="$environment",le="+Inf",stage="$stage"}[$__interval])
)

    -
    (
      sum by (action) (
  rate(registry_storage_action_seconds_bucket{action!="",env="$environment",environment="$environment",le="1",stage="$stage"}[$__interval])
)

    )
  )
  / ignoring(action) group_left()
  (
    sum by () (
  rate(registry_storage_action_seconds_bucket{action!="",env="$environment",environment="$environment",le="+Inf",stage="$stage"}[$__interval])
)

  )
) > 0
,1),0)

clamp_min(clamp_max((
  (
    sum by (type) (
  rate(gitlab_workhorse_object_storage_upload_time_bucket{env="$environment",environment="$environment",le="+Inf",stage="$stage",type!=""}[$__interval])
)

    -
    (
      sum by (type) (
  rate(gitlab_workhorse_object_storage_upload_time_bucket{env="$environment",environment="$environment",le="25",stage="$stage",type!=""}[$__interval])
)

    )
  )
  / ignoring(type) group_left()
  (
    sum by () (
  rate(gitlab_workhorse_object_storage_upload_time_bucket{env="$environment",environment="$environment",le="+Inf",stage="$stage",type!=""}[$__interval])
)

  )
) > 0
,1),0)

clamp_min(
  clamp_max(
    max by(environment, tier, type, stage, shard, job, instance) (
  clamp_min(
    clamp_max(
      (
        process_open_fds{env="$environment",environment="$environment",stage="$stage",type="google-cloud-storage"}
        /
        process_max_fds{env="$environment",environment="$environment",stage="$stage",type="google-cloud-storage"}
      )
      or
      (
        ruby_file_descriptors{env="$environment",environment="$environment",stage="$stage",type="google-cloud-storage"}
        /
        ruby_process_max_fds{env="$environment",environment="$environment",stage="$stage",type="google-cloud-storage"}
      )
      ,
      1)
  ,
  0)
)

  ,1)
,0)
