  (
    sum by (pod) (
  sli_aggregations:pilot_proxy_convergence_time_bucket:rate_5m{app="istiod",env="$environment",environment="$environment",job="pilot",le="+Inf",pod!=""}
)

    -
    (
      sum by (pod) (
