targets.expr:
clamp_min(clamp_max((
  (
    sum by (grpc_method) (
  rate(k8s_api_proxy_routing_duration_seconds_bucket{env="$environment",environment="$environment",grpc_method!="",le="+Inf",stage="$stage",status=~"success",type="kas"}[$__interval])
)

    -
    (
      sum by (grpc_method) (
  rate(k8s_api_proxy_routing_duration_seconds_bucket{env="$environment",environment="$environment",grpc_method!="",le="4.096",stage="$stage",status=~"success",type="kas"}[$__interval])
)

    )
  )
  / ignoring(grpc_method) group_left()
  (
    sum by () (
  rate(k8s_api_proxy_routing_duration_seconds_bucket{env="$environment",environment="$environment",grpc_method!="",le="+Inf",stage="$stage",status=~"success",type="kas"}[$__interval])
)

  )
) > 0
,1),0)

clamp_min(
  clamp_max(
    max by(environment, tier, type, stage, shard, job, instance) (
  clamp_min(
    clamp_max(
      (
        process_open_fds{env="$environment",environment="$environment",stage="$stage",type="kas"}
        /
        process_max_fds{env="$environment",environment="$environment",stage="$stage",type="kas"}
      )
      or
      (
        ruby_file_descriptors{env="$environment",environment="$environment",stage="$stage",type="kas"}
        /
        ruby_process_max_fds{env="$environment",environment="$environment",stage="$stage",type="kas"}
      )
      ,
      1)
  ,
  0)
)

  ,1)
,0)
