targets.expr:
clamp_min(clamp_max((
  (
    sum by (delivery_status_code) (
  rate(mailgun_delivery_time_seconds_bucket{delivery_status_code!="",env="ops",le="+Inf"}[$__interval])
)

    -
    (
      sum by (delivery_status_code) (
  rate(mailgun_delivery_time_seconds_bucket{delivery_status_code!="",env="ops",le="60"}[$__interval])
)

    )
  )
  / ignoring(delivery_status_code) group_left()
  (
    sum by () (
  rate(mailgun_delivery_time_seconds_bucket{delivery_status_code!="",env="ops",le="+Inf"}[$__interval])
)

  )
) > 0
,1),0)

clamp_min(
  clamp_max(
    max by(environment, tier, type, stage, shard, job, instance) (
  clamp_min(
    clamp_max(
      (
        process_open_fds{env="$environment",environment="$environment",type="mailgun"}
        /
        process_max_fds{env="$environment",environment="$environment",type="mailgun"}
      )
      or
      (
        ruby_file_descriptors{env="$environment",environment="$environment",type="mailgun"}
        /
        ruby_process_max_fds{env="$environment",environment="$environment",type="mailgun"}
      )
      ,
      1)
  ,
  0)
)

  ,1)
,0)
