targets.expr:
max by(persistentvolumeclaim) (
  kubelet_volume_stats_used_bytes{cluster=~"ops-gitlab-gke", namespace=~"$namespace"} /
  kubelet_volume_stats_capacity_bytes{cluster=~"ops-gitlab-gke", namespace=~"$namespace"}
)
and
count by(persistentvolumeclaim) (
  kube_persistentvolumeclaim_info{
    cluster=~"ops-gitlab-gke", namespace=~"$namespace",
    persistentvolumeclaim=~".*store-gateway.*"
  }
)
