targets.expr:
sum(
  rate(
    namedprocess_namegroup_cpu_seconds_total{
      environment="$environment",
      groupname="patroni",
      type="patroni",
      stage="main"
    }[$__interval]
  )
) without (mode)

max(
    namedprocess_namegroup_open_filedesc{
      environment="$environment",
      groupname="patroni",
      type="patroni",
      stage="main"
    }
) by (fqdn)

sum(
    namedprocess_namegroup_num_threads{
      environment="$environment",
      groupname="patroni",
      type="patroni",
      stage="main"
    }
) by (fqdn)

sum(
    namedprocess_namegroup_memory_bytes{
      environment="$environment",
      groupname="patroni",
      type="patroni",
      stage="main"
    }
) by (fqdn)

min(
  min_over_time(
    gitlab_service_apdex:ratio{environment="$environment", type="patroni", stage="main"}[$__interval] offset 1w
  )
) by (type)

max(
  max_over_time(
    gitlab_service_errors:ratio{environment="$environment", type="patroni", stage="main"}[$__interval] offset 1w
  )
) by (type)

max(
  avg_over_time(
    gitlab_service_ops:rate{environment="$environment", type="patroni", stage="main"}[$__interval] offset 1w
  )
) by (type)

clamp_min(clamp_max(
        1 -
        (
          (
            node_memory_MemFree_bytes{type="patroni", environment="$environment"} +
            node_memory_Buffers_bytes{type="patroni", environment="$environment"} +
            node_memory_Cached_bytes{type="patroni", environment="$environment"}
          )
        )
        /
        node_memory_MemTotal_bytes{type="patroni", environment="$environment"}
      ,1),0)

sort(
          clamp_min(
            clamp_max(
              max(
                gitlab_component_saturation:ratio:avg_over_time_1w{environment="$environment", type="patroni", stage=~"|main"}
                + 2 * gitlab_component_saturation:ratio:stddev_over_time_1w{environment="$environment", type="patroni", stage=~"|main"}
              ) by (component),
            1),
          0)
        )

sort(
          clamp_min(
            clamp_max(
              max(
                gitlab_component_saturation:ratio:predict_linear_2w{environment="$environment", type="patroni", stage=~"|main"}
                + 2 * gitlab_component_saturation:ratio:stddev_over_time_1w{environment="$environment", type="patroni", stage=~"|main"}
              ) by (component),
            1),
          0)
        )

clamp_min(clamp_max(
          max(
            gitlab_component_saturation:ratio{
              type="patroni",
              environment="$environment",
              stage=~"|main"
            }
          ) by (component)
          ,1),0)

clamp_min(
          clamp_max(
            max(
              gitlab_component_saturation:ratio:avg_over_time_1w{
                type="patroni",
                environment="$environment",
                stage=~"main|"
              }
            ) by (component)
          ,1),0)
