targets.expr:
clamp_min(
  clamp_max(
    max by(environment, tier, type, stage, shard, job, instance) (
  clamp_min(
    clamp_max(
      (
        process_open_fds{env="$environment",environment="$environment",type="patroni-registry"}
        /
        process_max_fds{env="$environment",environment="$environment",type="patroni-registry"}
      )
      or
      (
        ruby_file_descriptors{env="$environment",environment="$environment",type="patroni-registry"}
        /
        ruby_process_max_fds{env="$environment",environment="$environment",type="patroni-registry"}
      )
      ,
      1)
  ,
  0)
)

  ,1)
,0)

clamp_min(clamp_max(sum(
  rate(
    namedprocess_namegroup_cpu_seconds_total{groupname=~"pgbouncer.*", type="patroni-registry", environment="$environment"}[5m]
  )
) by (groupname, fqdn, type, stage, environment)
,1),0)
