targets.expr:
clamp_min(clamp_max((
  (
    sum by (fqdn) (
  rate(gitaly_praefect_replication_delay_bucket{env="$environment",environment="$environment",fqdn!="",job="praefect",le="+Inf",stage="$stage"}[$__interval])
)

    -
    (
      sum by (fqdn) (
  rate(gitaly_praefect_replication_delay_bucket{env="$environment",environment="$environment",fqdn!="",job="praefect",le="300",stage="$stage"}[$__interval])
)

    )
  )
  / ignoring(fqdn) group_left()
  (
    sum by () (
  rate(gitaly_praefect_replication_delay_bucket{env="$environment",environment="$environment",fqdn!="",job="praefect",le="+Inf",stage="$stage"}[$__interval])
)

  )
) > 0
,1),0)

clamp_min(clamp_max((
  (
    sum by (type) (
  rate(gitaly_praefect_replication_delay_bucket{env="$environment",environment="$environment",job="praefect",le="+Inf",stage="$stage",type!=""}[$__interval])
)

    -
    (
      sum by (type) (
  rate(gitaly_praefect_replication_delay_bucket{env="$environment",environment="$environment",job="praefect",le="300",stage="$stage",type!=""}[$__interval])
)

    )
  )
  / ignoring(type) group_left()
  (
    sum by () (
  rate(gitaly_praefect_replication_delay_bucket{env="$environment",environment="$environment",job="praefect",le="+Inf",stage="$stage",type!=""}[$__interval])
)

  )
) > 0
,1),0)

clamp_min(
  clamp_max(
    max by(environment, tier, type, stage, shard, job, instance) (
  clamp_min(
    clamp_max(
      (
        process_open_fds{env="$environment",environment="$environment",stage="$stage",type="praefect"}
        /
        process_max_fds{env="$environment",environment="$environment",stage="$stage",type="praefect"}
      )
      or
      (
        ruby_file_descriptors{env="$environment",environment="$environment",stage="$stage",type="praefect"}
        /
        ruby_process_max_fds{env="$environment",environment="$environment",stage="$stage",type="praefect"}
      )
      ,
      1)
  ,
  0)
)

  ,1)
,0)
