targets.expr:
clamp_min(clamp_max((
  (
    sum by (document_type) (
      rate(gitlab_sli_global_search_indexing_apdex_total{document_type!="",env="$environment",environment="$environment",stage="$stage"}[$__interval])
    )
    -
    sum by (document_type) (
      rate(gitlab_sli_global_search_indexing_apdex_success_total{document_type!="",env="$environment",environment="$environment",stage="$stage"}[$__interval])
    )
  )
  / ignoring (document_type) group_left()
  (
    sum by () (
      rate(gitlab_sli_global_search_indexing_apdex_total{document_type!="",env="$environment",environment="$environment",stage="$stage"}[$__interval])
    )
  )
) > 0
,1),0)

clamp_min(
  clamp_max(
    max by(environment, tier, type, stage, shard, job, instance) (
  clamp_min(
    clamp_max(
      (
        process_open_fds{env="$environment",environment="$environment",stage="$stage",type="search"}
        /
        process_max_fds{env="$environment",environment="$environment",stage="$stage",type="search"}
      )
      or
      (
        ruby_file_descriptors{env="$environment",environment="$environment",stage="$stage",type="search"}
        /
        ruby_process_max_fds{env="$environment",environment="$environment",stage="$stage",type="search"}
      )
      ,
      1)
  ,
  0)
)

  ,1)
,0)
