targets.expr:
clamp_min(clamp_max((
  sum by (api_version) (
  rate(sentry_events_latency_seconds_bucket{env="ops",environment="ops",job="statsd_exporter",le="+Inf",stage="$stage",type="sentry"}[$__interval])
)

  -
  (
    sum by (api_version) (
  rate(sentry_events_latency_seconds_bucket{env="ops",environment="ops",job="statsd_exporter",le="10",stage="$stage",type="sentry"}[$__interval])
)

  )
)
/ ignoring(api_version) group_left()
(
  sum by () (
  rate(sentry_events_latency_seconds_bucket{env="ops",environment="ops",job="statsd_exporter",le="+Inf",stage="$stage",type="sentry"}[$__interval])
)

)

,1),0)

clamp_min(clamp_max((
  sum by (status) (
  rate(sentry_events_latency_seconds_bucket{env="ops",environment="ops",job="statsd_exporter",le="+Inf",stage="$stage",type="sentry"}[$__interval])
)

  -
  (
    sum by (status) (
  rate(sentry_events_latency_seconds_bucket{env="ops",environment="ops",job="statsd_exporter",le="10",stage="$stage",type="sentry"}[$__interval])
)

  )
)
/ ignoring(status) group_left()
(
  sum by () (
  rate(sentry_events_latency_seconds_bucket{env="ops",environment="ops",job="statsd_exporter",le="+Inf",stage="$stage",type="sentry"}[$__interval])
)

)

,1),0)

clamp_min(
  clamp_max(
    max by(environment, tier, type, stage, job, instance) (
  clamp_min(
    clamp_max(
      (
        process_open_fds{env="$environment",environment="$environment",stage="$stage",type="sentry"}
        /
        process_max_fds{env="$environment",environment="$environment",stage="$stage",type="sentry"}
      )
      or
      (
        ruby_file_descriptors{env="$environment",environment="$environment",stage="$stage",type="sentry"}
        /
        ruby_process_max_fds{env="$environment",environment="$environment",stage="$stage",type="sentry"}
      )
      ,
      1)
  ,
  0)
)

  ,1)
,0)
