targets.expr:
clamp_min(clamp_max((
  (
    sum by (api_version) (
  sli_aggregations:sentry_events_latency_seconds_bucket:rate_5m{api_version!="",env="ops",environment="ops",job="statsd_exporter",le="+Inf",stage="$stage",type="sentry"}
)

    -
    (
      sum by (api_version) (
  sli_aggregations:sentry_events_latency_seconds_bucket:rate_5m{api_version!="",env="ops",environment="ops",job="statsd_exporter",le="10",stage="$stage",type="sentry"}
)

    )
  )
  / ignoring(api_version) group_left()
  (
    sum by () (
  sli_aggregations:sentry_events_latency_seconds_bucket:rate_5m{api_version!="",env="ops",environment="ops",job="statsd_exporter",le="+Inf",stage="$stage",type="sentry"}
)

  )
) > 0
,1),0)

clamp_min(clamp_max((
  (
    sum by (status) (
  sli_aggregations:sentry_events_latency_seconds_bucket:rate_5m{env="ops",environment="ops",job="statsd_exporter",le="+Inf",stage="$stage",status!="",type="sentry"}
)

    -
    (
      sum by (status) (
  sli_aggregations:sentry_events_latency_seconds_bucket:rate_5m{env="ops",environment="ops",job="statsd_exporter",le="10",stage="$stage",status!="",type="sentry"}
)

    )
  )
  / ignoring(status) group_left()
  (
    sum by () (
  sli_aggregations:sentry_events_latency_seconds_bucket:rate_5m{env="ops",environment="ops",job="statsd_exporter",le="+Inf",stage="$stage",status!="",type="sentry"}
)

  )
) > 0
,1),0)

clamp_min(
  clamp_max(
    max by(environment, tier, type, stage, shard, job, instance) (
  clamp_min(
    clamp_max(
      (
        process_open_fds{env="$environment",environment="$environment",stage="$stage",type="sentry"}
        /
        process_max_fds{env="$environment",environment="$environment",stage="$stage",type="sentry"}
      )
      or
      (
        ruby_file_descriptors{env="$environment",environment="$environment",stage="$stage",type="sentry"}
        /
        ruby_process_max_fds{env="$environment",environment="$environment",stage="$stage",type="sentry"}
      )
      ,
      1)
  ,
  0)
)

  ,1)
,0)
