clamp_max(
  sum by ()(
    sum by (stage_group,component) (
      label_replace(
        sum_over_time(
          gitlab:component:stage_group:execution:apdex:success:rate_1h{environment="$environment",monitor="global",stage="$stage",stage_group="certify"}[28d]
