sum by (controller, action) (
  rate(gitlab_transaction_duration_seconds_count{
    environment='$environment',
    stage='$stage',
    feature_category=~'(requirements_management|quality_management|service_desk)',
    type='web',
    controller=~'$controller',
    action=~'$action'
  }[$__interval])
)

avg(
  avg_over_time(
