targets.expr:
sum by (controller, action) (
  rate(gitlab_transaction_duration_seconds_count{
    environment='$environment',
    stage='$stage',
    feature_category=~'(code_quality|code_testing|load_testing|browser_performance|usability_testing|accessibility_testing)',
    type='web',
    controller=~'$controller',
    action=~'$action'
  }[$__interval])
)

avg(
  avg_over_time(
    controller_action:gitlab_transaction_duration_seconds:p95{
      environment="$environment",
      stage='$stage',
      action=~"$action",
      controller=~"$controller",
      feature_category=~'(code_quality|code_testing|load_testing|browser_performance|usability_testing|accessibility_testing)',
      type='web'
    }[$__interval]
  )
) by (controller, action)

sum without (fqdn,instance) (
  rate(
    gitlab_sql_duration_seconds_count{
      environment="$environment",
      stage='$stage',
      action=~"$action",
      controller=~"$controller",
      feature_category=~'(code_quality|code_testing|load_testing|browser_performance|usability_testing|accessibility_testing)',
      type='web'
    }[$__interval]
  )
)
